CREATE OR REPLACE FUNCTION set_updated_at()
  RETURNS TRIGGER AS $$
BEGIN
  NEW.updated_at := EXTRACT(EPOCH FROM now());
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION set_created_at()
  RETURNS TRIGGER AS $$
BEGIN
  NEW.created_at := EXTRACT(EPOCH FROM now());
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;
